import React from 'react';
import ReactDOM from 'react-dom/client';
import App from './App';

const rootElement = document.getElementById('root');
if (!rootElement) {
  throw new Error("Could not find root element to mount to");
}

const root = ReactDOM.createRoot(rootElement);
root.render(
  <React.StrictMode>
    <App />
  </React.StrictMode>
);

export interface Project {
  title: string;
  description: string;
  tags: string[];
  link: string;
  image: string;
  category: 'Website' | 'Tool' | 'SEO';
}

export interface VoiceState {
  isActive: boolean;
  isConnecting: boolean;
  isError: boolean;
  transcription: string;
  aiTranscription: string;
}

import React, { useState, useEffect } from 'react';
import Hero from './components/Hero';
import About from './components/About';
import Services from './components/Services';
import WhyMe from './components/WhyMe';
import Portfolio from './components/Portfolio';
import Contact from './components/Contact';
import VoiceAssistant from './components/VoiceAssistant';

const App: React.FC = () => {
  const [activeSection, setActiveSection] = useState('hero');

  useEffect(() => {
    const handleScroll = () => {
      const sections = ['hero', 'about', 'services', 'portfolio', 'contact'];
      for (const section of sections) {
        const element = document.getElementById(section);
        if (element) {
          const rect = element.getBoundingClientRect();
          if (rect.top <= 100 && rect.bottom >= 100) {
            setActiveSection(section);
          }
        }
      }
    };
    window.addEventListener('scroll', handleScroll);
    return () => window.removeEventListener('scroll', handleScroll);
  }, []);

  const scrollTo = (id: string) => {
    const el = document.getElementById(id);
    if (el) el.scrollIntoView({ behavior: 'smooth' });
  };

  return (
    <div className="min-h-screen font-sans selection:bg-blue-100 selection:text-blue-900">
      {/* Navigation */}
      <nav className="fixed top-0 left-0 right-0 bg-white/80 backdrop-blur-md border-b border-gray-100 z-50">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between h-16 items-center">
            <div className="flex items-center gap-2 cursor-pointer" onClick={() => scrollTo('hero')}>
              <div className="w-8 h-8 bg-blue-600 rounded-lg flex items-center justify-center text-white font-bold">A</div>
              <span className="text-xl font-bold text-gray-900 hidden sm:block">Anwar Ali Sehar</span>
            </div>
            <div className="flex space-x-4 md:space-x-8">
              {['About', 'Services', 'Portfolio', 'Contact'].map((item) => (
                <button
                  key={item}
                  onClick={() => scrollTo(item.toLowerCase())}
                  className={`text-sm font-medium transition-colors ${
                    activeSection === item.toLowerCase() ? 'text-blue-600' : 'text-gray-500 hover:text-gray-900'
                  }`}
                >
                  {item}
                </button>
              ))}
            </div>
          </div>
        </div>
      </nav>

      <main className="pt-16">
        <Hero onCtaClick={() => scrollTo('contact')} onToolsClick={() => scrollTo('portfolio')} />
        <About />
        <Services />
        <WhyMe />
        <Portfolio />
        <Contact />
      </main>

      <footer className="bg-gray-900 text-white py-12">
        <div className="max-w-7xl mx-auto px-4 text-center">
          <p className="text-xl font-bold mb-4">Anwar Ali Sehar</p>
          <p className="text-gray-400 mb-8 max-w-md mx-auto">
            Empowering Local Businesses with Smart SEO & AI Solutions.
          </p>
          <div className="flex justify-center space-x-6 mb-8">
            <a href="https://web.facebook.com/arshad.ali.7146557" target="_blank" className="hover:text-blue-400 transition-colors"><i className="fab fa-facebook-f text-xl"></i></a>
            <a href="https://www.linkedin.com/in/anwar-sehar" target="_blank" className="hover:text-blue-400 transition-colors"><i className="fab fa-linkedin-in text-xl"></i></a>
            <a href="https://github.com/Markiting11" target="_blank" className="hover:text-blue-400 transition-colors"><i className="fab fa-github text-xl"></i></a>
          </div>
          <p className="text-sm text-gray-500">¬© {new Date().getFullYear()} Anwar Ali Sehar. All Rights Reserved.</p>
        </div>
      </footer>

      {/* Persistent Voice Assistant */}
      <VoiceAssistant />
    </div>
  );
};

export default App;

