      audioContextRef.current = new (window.AudioContext || (window as any).webkitAudioContext)({ sampleRate: 16000 });
      outAudioContextRef.current = new (window.AudioContext || (window as any).webkitAudioContext)({ sampleRate: 24000 });

      const stream = await navigator.mediaDevices.getUserMedia({ audio: true });

      const sessionPromise = ai.live.connect({
        model: 'gemini-2.5-flash-native-audio-preview-12-2025',
        config: {
          responseModalities: [Modality.AUDIO],
          systemInstruction: SYSTEM_INSTRUCTION,
          speechConfig: {
            voiceConfig: { prebuiltVoiceConfig: { voiceName: 'Kore' } }
          },
          inputAudioTranscription: {},
          outputAudioTranscription: {}
        },
        callbacks: {
          onopen: () => {
            setIsConnecting(false);
            setIsActive(true);
            const source = audioContextRef.current!.createMediaStreamSource(stream);
            const scriptProcessor = audioContextRef.current!.createScriptProcessor(4096, 1, 1);

            scriptProcessor.onaudioprocess = (e) => {
              const inputData = e.inputBuffer.getChannelData(0);
              const int16 = new Int16Array(inputData.length);
              for (let i = 0; i < inputData.length; i++) {
                int16[i] = inputData[i] * 32768;
              }
              const pcmBlob = {
                data: encode(new Uint8Array(int16.buffer)),
                mimeType: 'audio/pcm;rate=16000'
              };
              sessionPromise.then(s => {
                if (s && typeof s.sendRealtimeInput === 'function') {
                  s.sendRealtimeInput({ media: pcmBlob });
                }
              }).catch(() => {});
            };

            source.connect(scriptProcessor);
            scriptProcessor.connect(audioContextRef.current!.destination);
          },
          onmessage: async (message: LiveServerMessage) => {
            // Handle transcriptions
            if (message.serverContent?.inputTranscription) {
               setHistory(prev => {
                 const last = prev[prev.length - 1];
                 if (last?.type === 'user') {
                    const updated = [...prev];
                    updated[updated.length - 1] = { ...last, text: last.text + message.serverContent!.inputTranscription!.text };
                    return updated;
                 }
                 return [...prev, { type: 'user', text: message.serverContent!.inputTranscription!.text }];
               });
            }
            if (message.serverContent?.outputTranscription) {
               setHistory(prev => {
                 const last = prev[prev.length - 1];
                 if (last?.type === 'ai') {
                    const updated = [...prev];
                    updated[updated.length - 1] = { ...last, text: last.text + message.serverContent!.outputTranscription!.text };
                    return updated;
                 }
                 return [...prev, { type: 'ai', text: message.serverContent!.outputTranscription!.text }];
               });
            }

            // Handle Audio
            const audioData = message.serverContent?.modelTurn?.parts[0]?.inlineData?.data;
            if (audioData && outAudioContextRef.current && outAudioContextRef.current.state !== 'closed') {
              const audioBuffer = await decodeAudioData(decode(audioData), outAudioContextRef.current, 24000, 1);
              const source = outAudioContextRef.current.createBufferSource();
              source.buffer = audioBuffer;
              source.connect(outAudioContextRef.current.destination);

              nextStartTimeRef.current = Math.max(nextStartTimeRef.current, outAudioContextRef.current.currentTime);
              source.start(nextStartTimeRef.current);
              nextStartTimeRef.current += audioBuffer.duration;
              sourcesRef.current.add(source);
              source.onended = () => sourcesRef.current.delete(source);
            }

            if (message.serverContent?.interrupted) {
              sourcesRef.current.forEach(s => {
                try { s.stop(); } catch {}
              });
              sourcesRef.current.clear();
              nextStartTimeRef.current = 0;
            }
          },
          onclose: () => stopSession(),
          onerror: (e) => {
            console.error('Session error:', e);
            stopSession();
          }
        }
      });

      sessionRef.current = await sessionPromise;
    } catch (err) {
      console.error('Failed to start session:', err);
      setIsConnecting(false);
      setIsActive(false);
    }
  };

  const stopSession = () => {
    if (sessionRef.current) {
       try { sessionRef.current.close(); } catch {}
       sessionRef.current = null;
    }

    if (audioContextRef.current && audioContextRef.current.state !== 'closed') {
      audioContextRef.current.close().catch(console.error);
    }
    audioContextRef.current = null;

    if (outAudioContextRef.current && outAudioContextRef.current.state !== 'closed') {
      outAudioContextRef.current.close().catch(console.error);
    }
    outAudioContextRef.current = null;

    setIsActive(false);
    setIsConnecting(false);
    sourcesRef.current.forEach(s => {
      try { s.stop(); } catch {}
    });
    sourcesRef.current.clear();
    nextStartTimeRef.current = 0;
  };

  useEffect(() => {
    return () => stopSession();
  }, []);

  return (
    <div className="fixed bottom-6 right-6 z-[60] flex flex-col items-end">
      {/* Assistant Window */}
      {isOpen && (
        <div className="mb-4 w-80 sm:w-96 h-[450px] bg-white rounded-3xl shadow-2xl border border-gray-100 flex flex-col overflow-hidden animate-in slide-in-from-bottom-4 duration-300">
          <div className="p-4 bg-blue-600 text-white flex justify-between items-center">
            <div className="flex items-center gap-3">
              <div className={`w-3 h-3 rounded-full ${isActive ? 'bg-green-400 animate-pulse' : 'bg-white/30'}`}></div>
              <span className="font-bold">Anwar's AI Assistant</span>
            </div>
            <button onClick={toggleAssistant} className="hover:bg-white/20 p-1 rounded-lg"><i className="fas fa-times"></i></button>
          </div>

          <div className="flex-1 p-4 overflow-y-auto bg-gray-50 flex flex-col gap-3">
            {history.length === 0 && (
              <div className="text-center py-8">
                <div className="w-16 h-16 bg-blue-100 rounded-full flex items-center justify-center mx-auto mb-4 text-blue-600">
                  <i className="fas fa-robot text-2xl"></i>
                </div>
                <p className="text-gray-500 text-sm">Hi! Main Anwar ka AI assistant hoon. Aap mujhse English ya Urdu mein services ke baare mein pooch sakte hain.</p>
              </div>
            )}
            {history.map((msg, i) => (
              <div key={i} className={`max-w-[85%] p-3 rounded-2xl text-sm ${msg.type === 'user' ? 'bg-blue-100 text-blue-900 self-end' : 'bg-white border border-gray-100 text-gray-700 self-start shadow-sm'}`}>
                {msg.text}
              </div>
            ))}
            {isConnecting && (
              <div className="text-center text-gray-400 text-xs italic">Connecting to Live API...</div>
            )}
          </div>

          <div className="p-4 bg-white border-t border-gray-100 flex items-center gap-3">
            {isActive ? (
              <button 
                onClick={stopSession}
                className="w-full py-3 bg-red-500 text-white rounded-xl font-bold flex items-center justify-center gap-2 hover:bg-red-600 transition-colors"
              >
                <i className="fas fa-stop"></i> Stop Voice
              </button>
            ) : (
              <button 
                onClick={startSession}
                disabled={isConnecting}
                className={`w-full py-3 ${isConnecting ? 'bg-gray-300' : 'bg-blue-600 hover:bg-blue-700'} text-white rounded-xl font-bold flex items-center justify-center gap-2 transition-colors`}
              >
                {isConnecting ? (
                  <i className="fas fa-spinner fa-spin"></i>
                ) : (
                  <>
                    <i className="fas fa-microphone"></i> Start Voice Chat
                  </>
                )}
              </button>
            )}
          </div>
        </div>
      )}

      {/* Floating Toggle Button */}
      <button
        onClick={toggleAssistant}
        className={`w-16 h-16 rounded-full flex items-center justify-center text-white shadow-2xl transition-all transform hover:scale-110 active:scale-95 ${
          isActive ? 'bg-red-500' : 'bg-blue-600'
        }`}
      >
        <i className={`fas ${isOpen ? 'fa-comment-slash' : (isActive ? 'fa-microphone-lines' : 'fa-headset')} text-2xl`}></i>
        {isActive && !isOpen && (
          <span className="absolute -top-1 -right-1 flex h-4 w-4">
            <span className="animate-ping absolute inline-flex h-full w-full rounded-full bg-red-400 opacity-75"></span>
            <span className="relative inline-flex rounded-full h-4 w-4 bg-red-500"></span>
          </span>
        )}
      </button>
    </div>
  );
};

